package com.jio.ranshjmd;

import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.os.Environment;
import android.util.Base64;

import java.io.ByteArrayOutputStream;
import java.io.File;
import java.io.FileInputStream;
import java.io.IOException;

public class Base64Image {

    public static String encode(String filePath) {
        Bitmap selectedImage = BitmapFactory.decodeFile(filePath);
        ByteArrayOutputStream stream = new ByteArrayOutputStream();
        selectedImage.compress(Bitmap.CompressFormat.JPEG, 100, stream);
        byte[] byteArray = stream.toByteArray();
        return android.util.Base64.encodeToString(byteArray, 0);
    }

    public static byte[] decode(String base64String) {
        return android.util.Base64.decode(base64String, android.util.Base64.DEFAULT);
    }

    public static void example() {
        String filePath = Environment.getExternalStorageDirectory() + "/aDirectory/image.jpg";
        String encodedString = encode(filePath);

        byte[] decodedString = decode(encodedString);
        //remember to strip off any leading string like "data:image/gif;base64,", otherwise it will not work
        String hardcodedString = "";
        Bitmap decodedByte = BitmapFactory.decodeByteArray(decodedString, 0, decodedString.length);
        //yourImageView.setImageBitmap(decodedByte);
    }

    public static String getBase64FromPath(String path) {
        String base64 = "";
        try {/*from   w w w .  ja  va  2s  .  c om*/
            File file = new File(path);
            byte[] buffer = new byte[(int) file.length() + 100];
            @SuppressWarnings("resource")
            int length = new FileInputStream(file).read(buffer);
            base64 = Base64.encodeToString(buffer, 0, length,
                    Base64.NO_WRAP);
        } catch (IOException e) {
            e.printStackTrace();
        }
        return base64;
    }
}
